// Get translated setting description
function getSettingTranslation(settingName, field) {
    // If English or no translation available, return null (use default from settings-data.js)
    if (currentLanguage === 'en') return null;
    
    const langTranslations = settingTranslations[currentLanguage];
    if (!langTranslations) return null;
    
    const settingTrans = langTranslations[settingName];
    if (!settingTrans) return null;
    
    return settingTrans[field] || null;
}
